import React, { Component } from "react";
import about from './images/simonAboutImage.png';
import Butter from 'buttercms';
import { Link } from 'react-router-dom'
import '../butterclient';
import { Helmet } from "react-helmet";

const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77');

export default class LinkHandlerSubcategorys extends Component {
    constructor(props) {
        super(props);
        this.sitelinks = ["Men", "Woman", "About", "SALE"]
        this.props = props;
        this.data = {}
        this.state = { data:{
        slug: "home",
        name: "Home",
        page_type: null,
        fields: {
          sitetype: "html-page",
          aboutpage: "<h>Loading...</h>\n<img src=\" \"/>\n<p>Hang in there!</p>\n"


        }}, 
        sitePosition : window.location.href.replace("http://localhost:3000/", "").split("/")
                      };
        this.getPageInformation(this.state.sitePosition);

      }

    getPageLink(){

        let ProcessedCurrentPageLocation = window.location.href.replace("http://localhost:3000/", "").split("/");
        if (ProcessedCurrentPageLocation[0] !== this.state.sitePosition[0] || ProcessedCurrentPageLocation[1] !== this.state.sitePosition[1]) {
            this.state.sitePosition = ProcessedCurrentPageLocation;
            this.getPageInformation(this.state.sitePosition);
            }


      }

      setError(err){

        console.log(err.error.response.status + " " +err.error.response.statusText);
        let pagenotFond = {data:{
          slug: "home",
          name: "Home",
          page_type: null,
          fields: {
            sitetype: "html-page",
            aboutsegment:[{
                "aboutsegment": "<!DOCTYPE html>\n<p>\nWe offer €4.99 standard international shipping on all orders. We ship globally and time of delivery depends on country of resident but we aim to deliver your order between 3-5 days.</p>\n<span>Depending on where you live there might be added duties/ import costs when collecting your order. (Basically all destinations outside the EU) We didn’t make these rules, but always here to help</span>",
                "aboutsegmentname": "",
                "sitetype": ""
              },],
            aboutpage: '<div className="pageWrapper"><div className="pageTopDivider"></div><div className="text-center"><h className="headerPageText" >' + err.error.response.status + " " +err.error.response.statusText + '</h><a href="/"><h1>Click here to go to homepage</h1></a></div></div>' 
          }}}

          this.setState(pagenotFond);

      }

      setData(resp){
        if (resp.data !== this.state.data){
          this.setState(resp.data);
        }

      }


  async getPageInformation(link) {
    if(link === "home" || link === "Home"){
      link ="ERROR"
    }
     link = link[0].toLowerCase();
    await butter.page.retrieve('*', ''+link +'').then(resp => this.setData(resp)).catch(err=>this.setError({error:err}));

    }

    CreatePageHTML(pagedata){
      //let data = this.state.data;
     if (pagedata === undefined){
        let containor = <div className="pageWrapper"><div className="pageTopDivider"></div><div className="text-center"><h className="headerPageText" > 404 Page Not Found</h><a href="/"><h1>Click here to go to homepage</h1></a></div></div>
         return(containor)
     }
     else{
        let pagecontent = (pagedata);
        let containor = <div dangerouslySetInnerHTML={{ __html: pagecontent}} />


      return (containor)
     }

    }

    CreatePageSHOP(product){
    let productname = product.product_name
    let image = product.product_picture
    let subcatagoryname = product.subcategoryname
    const productcard = <div><img src={image} style={{"width": "80%"}}  /><p>{productname}</p> </div>;
    return <div className="column">
      <div className="row">
      <Link to={"/"+this.state.sitePosition[0]+"/"+subcatagoryname+"/"+productname}> {productcard} </Link>
      </div>
    </div>
    }

    getPageToCreate(pagedata){
      if(pagedata.fields.sitetype === "html-page"){
        return this.CreatePageHTML(this.state.data.fields.aboutsegment.filter(segment => segment.aboutsegmentname === this.state.sitePosition[1].toLowerCase()))
      }
      if(pagedata.fields.sitetype === "shop-page"){
        return this.state.data.fields.subcategory.product_in_subcategory.filter(product => product.subcategoryname === this.state.sitePosition[1].toLowerCase()).map(product => this.CreatePageSHOP(product))
      }
    }


  render() {
    console.log(this.state);
    this.getPageLink();
    return (
      <div className="pageWrapper">        
        <div className="pageTopDivider"></div>
        <div className="center">
        {this.getPageToCreate(this.state.data)} 
        </div>
        <div className="divider"></div>
        <div className="divider"></div>
      </div>
    );
  }


}
