import React, { Component } from "react";
import about from './images/simonAboutImage.png';
import Butter from 'buttercms';
import { Link } from 'react-router-dom'
import '../butterclient';
import { Helmet } from "react-helmet";

const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77');

export default class LinkHandler extends Component {
    constructor(props) {
        super(props);
        this.sitelinks = ["Men", "Woman", "About", "SALE"]
        this.props = props;
        this.state = { data:{
        slug: "home",
        name: "Home",
        page_type: null,
        fields: {
          aboutpage: "<h>Loading...</h>\n<img src=\" \"/>\n<p>Hang in there!</p>\n"


        }}, 
        sitePosition : "about"
                      };

        function hashHandler() {
                        console.log('The hash has changed!');
                      }

        window.addEventListener('hashchange', hashHandler, false);
        this.getPageLink();
        this.getPageInformation(this.state.sitePosition);


      }
    onRouteChanged() {
        console.log("ROUTE CHANGED");
      }

    getPageLink(){
        let currentPageLocation = window.location.href;
        let ProcessedCurrentPageLocation = currentPageLocation.replace("http://localhost:3000/", "")
        let link = {Position:{sitePosition : ProcessedCurrentPageLocation} }
        console.log(link); 
        if (link !== this.state.sitePosition) {
        this.state.sitePosition = ProcessedCurrentPageLocation;
        getPageInformation(this.state.sitePosition);
        }
      }




  async getPageInformation(link) {
     link = link.toLowerCase();
      const resp = await butter.page.retrieve('*', ''+link +'');

      console.log(resp)
      this.setState(resp.data);


    }

    CreatePage(pagedata){
      //let data = this.state.data;

      let pagecontent = (pagedata);
    let containor = <div dangerouslySetInnerHTML={{ __html: pagecontent}} />


      return (containor) //pagedata.map(p => {return p.homesegment});
      //data.map(page => {console.log(page, page.map((pagesegment) =>{pagesegment}))})
    }


  render() {
    console.log("detta är statet:")
    console.log(this.state)
    getPageLink()
    return (
      <div className="pageWrapper">        
        <div className="pageTopDivider"></div>
        <div className="center">
        {this.CreatePage(this.state.data.fields.aboutpage)} 
        </div>
        <div className="divider"></div>
        <div className="divider"></div>
      </div>
    );
  }


}

function ImageLoaderAbout() {
  // Import result is the URL of your image
  return <img className="showCaseImage" src={about} alt="Logo" />;
}

function ImageLoaderPhotography() {
  // Import result is the URL of your image
  return <img className="showCaseImage" src={about} alt="Logo" />;
}
