import React from 'react';
import Faq from "./FAQ.js";
import Basket from "./Basket.js";
import Back from "./Back";
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom'
import Butter from "buttercms";

const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77')

export default class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: {
                          fields: {navbar: [
                              {

                                fields: {
                                  headline: "...",
                                  categories: [
                                    {
                                      category: "Loading... "
                                    }

                                  ]
                                }
                              }
                            ]
                          }

    },
                  showFAQ: false ,
                  showBasket:false,
                  itemsInBasket:this.props.model.getBasket()
                   };

  }

  async componentDidMount () {
    const resp = await butter.page.retrieve('*', 'navbar');
    this.setState(resp.data);
    this.setState({itemsInBasket: this.props.model.getBasket().length})
  }

  async componentDidUpdate(){
    if(this.state.itemsInBasket !== this.props.model.getBasket.length)
    this.setState({itemsInBasket: this.props.model.getBasket.length})
  }




  RenderBack() {
    let location = useLocation();
    if (location.pathname ==="/"){
        return null;
    }
    else{
        return <Back />;
    }

}

  toggleFAQ() {
    this.setState({
      showFAQ: !this.state.showFAQ,
    });
  }

  toggleBASKET() {
    this.setState({
      showBasket: !this.state.showBasket,
    });
  }

  renderCatagories(category){
    let catagoryName = category.fields.headline
    let catagoryCatagory = category.fields.categories

    return <div className="NavUntilHover"><p><Link to={"/"+catagoryName}>{catagoryName}</Link></p>
    <div className="hiddenNavUntilHover">
      <table className="menuTable">
        <tr>
          {catagoryCatagory.map(cat => {
            return <tr><Link  to={"/"+catagoryName+"/"+cat.category}>{cat.category}</Link></tr>
            })}
        </tr>
      </table>
      </div>
      </div>

    }

  render() {


    let navbar = this.state.data.fields.navbar;
    return <div className="header">
        <p className="BASKETbutton" onClick={this.toggleBASKET.bind(this)}>{"BASKET(" + this.state.itemsInBasket +")"}</p>
        <Link to="/" style={{color: 'black', "textDecoration":"none" }} >
        <p className = "headerName">Projekt Gr√∂n</p>
        </Link>
        <div className="navibar"> 
          {navbar.map(categor => {return this.renderCatagories(categor)})}
        </div>
        <this.RenderBack/>
        <div className="FAQ" >{this.state.showFAQ ? <Faq closeFAQ={this.toggleFAQ.bind(this)} /> : null}</div>
        <div className="FAQ" >{this.state.showBasket ? <Basket model={this.props.model} closeBASKET={this.toggleBASKET.bind(this)} /> : null}</div>
    </div>


  }





}
