import React from "react";
import Faq from "./FAQ.js";
import Basket from "./Basket.js";
import Back from "./Back";
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom'

export default class Header extends React.Component {
  constructor(props) {

    super();
    this.props = props;
    this.model = this.props.model;



    this.state = { showFAQ: false ,
                   showBasket:false,
                   data: {
                    fields: {
                      hero_component: []
                    }
                  }};

  }

  async componentDidMount () {
    const { match } = this.props
    const resp = await butter.page.retrieve('*', 'front-page')
    this.setState(resp.data)
  }

  RenderBack() {
    let location = useLocation();
    if (location.pathname ==="/"){
        return null;
    }
    else{
        return <Back />;
    }

}

  toggleFAQ() {
    this.setState({
      showFAQ: !this.state.showFAQ,
    });
  }

  toggleBASKET() {
    this.setState({
      showBasket: !this.state.showBasket,
    });
  }

  renderCatagories(category){
    console.log(category);
    return <div className="NavUntilHover"><p>{category.Atrib}</p>
    <div className="hiddenNavUntilHover">
      <table>
        <tr>{category.Categories.map(cat => {return <tr>{cat}</tr>}) }</tr>
      </table>

      </div>
      </div>

    }

  render() {
    return <div className="header">

        <p className="FAQbutton" onClick={this.toggleFAQ.bind(this)}>CONTACT</p>
  <p className="BASKETbutton" onClick={this.toggleBASKET.bind(this)}>BASKET({()=>{ return this.props.model.getBasketItemNumber().toString()}})</p>
        <Link to="/" style={{color: 'black', "textDecoration":"none" }} >
        <p className = "headerName">Projekt Gr√∂n</p>
        </Link>
        <div className="navibar"> 
          {this.categorys.map(categor => {return this.renderCatagories(categor)})}
        </div>
        <this.RenderBack/>
        <div className="FAQ" >{this.state.showFAQ ? <Faq closeFAQ={this.toggleFAQ.bind(this)} /> : null}</div>
        <div className="FAQ" >{this.state.showBasket ? <Basket closeBASKET={this.toggleBASKET.bind(this)} /> : null}</div>
    </div>


  }





}
