    }
    this.state.basket = [...bask, productObject];
    localStorage.setItem("The_coolest_store", 
        JSON.stringify( {basket:[...this.state.basket]}))
  }

  removeFromBasket(product,size){
    let bask = this.state.basket.filter(produc => ((deepEqual(produc.prod, product) === false) && (produc.sizeselected !== size))===false);
    this.state.basket = [...bask];
    localStorage.setItem("The_coolest_store", 
