    let productthatexisted = this.state.basket.filter(produc => ((deepEqual(produc.prod, prod) === true) && (produc.sizeselected === size))===true);
    let bask = this.state.basket.filter(produc => ((deepEqual(produc.prod, prod) === false) && (produc.sizeselected !== size))===true);
