        "name": "Home",
        "page_type": null,
        "fields": {
          "homepage": {
            "homesegments": [
              {
                "homesegment": "<h>Hej Detta har jag läst in från vårat API</h>\n<img src=\" \"/>\n<p>Nu vill jag bara testa lite </p>\n"
              }
            ]
          }
        }},
          sitePosition : "home"
                      };
        this.getPageInformation(this.state.sitePosition)
      }


  async getPageInformation(link) {
      const resp = await butter.page.retrieve('*', ''+link+'');
      this.setState(resp.data);


    }

