import React from 'react';
import { Link } from "react-router-dom";


export default class Basket extends React.Component {

  constructor(props){
    super();
    this.state = {basket:[]}
    this.props = props;


  }

  componentDidMount() {
    this.getBasket();
  }

  componentWillUnmount() {
  }

  getBasket(){
    let bas =  this.props.model.getBasket();

    if(JSON.stringify(this.state.basket) != JSON.stringify(bas)){

      this.setState({
        basket:bas
      });

    }


  }

  basketRender(basket){
    if (basket.length === 0){
      return <table className="basketTable"><tr><tr>BASKET I EMPTY</tr></tr></table>
    }
    else{
      return <table className="basketTable">
        <tr><td>cuantay</td><td>Size</td><td>Product Name</td><td>Price</td></tr>
        {basket.map(basketItem => <tr><td><button>-</button>{basketItem.amountorderd}<button>+</button></td><td>{basketItem.sizeselected}</td><td>{basketItem.prod.product_name}</td><td>{basketItem.prod.product_price}</td></tr>)}
        </table>
    }
  }

  clearer(){
    this.props.model.clearBasket(); 
    this.getBasket();


  }

    render() {
      let basket = this.state.basket;

      return (

        <div className='popup' >

          <div className='popup_inner'>
          <div className="divider"></div>
          <div className="divider"></div>
            <p className="headerBasket">BASKET</p>
            <div className="divider"></div>
            <button onClick={() => this.clearer()}>CLEAR BASKET</button>
            <div className="divider"></div>
            {this.basketRender(basket)}
            <div className="divider"></div>
            <div className="divider"></div>
            <Link to={"/checkout"}> GO TO CHECKOUT</Link>
            <div className="divider"></div>
            <div className="divider"></div>
          <p className = "closeEx" onClick={this.props.closeBASKET}>x</p>

          </div>
        </div>

      );
    }
  }
