import React from 'react';
import { Link } from "react-router-dom";


export default class Basket extends React.ComponentÂ {

  constructor(props){
    super();
    this.state = {basket:[]}
    this.props = props;


  }

  componentDidMount() {
    this.getBasket();
  }

  componentWillUnmount() {
  }

  getBasket(){
    let bas =  this.props.model.getBasket();

    if(JSON.stringify(this.state.basket) != JSON.stringify(bas)){

      this.setState({
        basket:bas
      });

    }


  }

  basketRender(basket){
    if (basket.length === 0){
      return <table className="basketTable"><tr><tr>BASKET I EMPTY</tr></tr></table>
    }
    else{
      return <table className="basketTable">{basket.map(basketItem => <tr><tr>{basketItem.product_name}</tr><tr>{basketItem.product_price}</tr></tr>)}</table>
    }
  }

  clearer(){
    this.props.model.clearBasket(); 
    this.getBasket();


  }

    render() {

      return (

        <div className='popup' >

          <div className='popup_inner'>
          <div className="divider"></div>
          <div className="divider"></div>
            <p className="headerName">BASKET</p>
            <div className="divider"></div>
            {this.basketRender(this.state.basket)}
            <div className="divider"></div>
            <div className="divider"></div>
            <Link to={"/checkout"}> GO TO CHECKOUT</Link>
            <button onClick={() => this.clearer()}>CLEAR BASKET</button>
          <p className = "closeEx" onClick={this.props.closeBASKET}>x</p>

          </div>
        </div>

      );
    }
  }
