import React, { Component } from "react";

import Butter from 'buttercms';
import { Link } from 'react-router-dom'
import '../butterclient';


const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77');

export default class LinkHandlerSubcategorysOrProducts extends Component {
    constructor(props) {
        super(props);
        this.sitelinks = ["Men", "Woman", "About", "SALE"]
        this.props = props;
        this.data = {}
        this.state = { data:{
        slug: "home",
        name: "Home",
        page_type: null,
        fields: {
          sitetype: "loading",
          aboutsegment:[{
            aboutsegment: "<div><h>Loading...</h> <p>Hang in there!</p></div>",
            aboutsegmentname: "",
            sitetype: ""
          },],
          aboutpage: "<h>Loading...</h>\n<img src=\" \"/>\n<p>Hang in there!</p>\n"


        }}, 
        sitePosition : window.location.href.replace("http://localhost:3000/", "").split("/")
                      };
        this.getPageInformation(this.state.sitePosition);

      }

    getPageLink(){

        let ProcessedCurrentPageLocation = window.location.href.replace("http://localhost:3000/", "").split("/");
        if (ProcessedCurrentPageLocation[0] !== this.state.sitePosition[0] || ProcessedCurrentPageLocation[1] !== this.state.sitePosition[1]|| ProcessedCurrentPageLocation[2] !== this.state.sitePosition[2]) {
            this.state.sitePosition = ProcessedCurrentPageLocation;
            this.getPageInformation(this.state.sitePosition);
            }


      }

      setError(err){
        console.log(err);
        let erroris = err.error.data.detail;
        let pagenotFond = {data:{
          slug: "home",
          name: "Home",
          page_type: null,
          fields: {
            sitetype: "error-page",
            aboutpage: '<div className="pageWrapper"><div className="pageTopDivider"></div><div className="text-center"><h className="headerPageText" >' + {erroris}+ '</h><a href="/"><h1>Click here to go to homepage</h1></a></div></div>' 
          }}}

          this.setState(pagenotFond);

      }

      setData(resp){
        if (resp.data !== this.state.data){
          this.setState(resp.data);
        }

      }


  async getPageInformation(link) {
    if(link === "home" || link === "Home"){
      link ="ERROR"
    }
     link = link[0].toLowerCase();
    await butter.page.retrieve('*', ''+link +'').then(resp => this.setData(resp)).catch(err=>this.setError({error:err}));

    }

    CreatePageHTML(pagedata){

      //let data = this.state.data;
     if (pagedata === undefined || pagedata[0] === undefined){
        let containor = (<div className="pageWrapper"><div className="pageTopDivider"></div><div className="text-center"><h className="headerPageText" >404 Page not found</h><a href="/"><h1>Click here to go to homepage</h1></a></div></div>);
         return(containor)
     }
     else{
        let pagecontent = pagedata[0].aboutsegment
        let containor = <div dangerouslySetInnerHTML={{ __html: pagecontent}} />


      return (containor)
     }

    }

    sizerender(size){
      let si = size.sizes;
      console.log(si);
      if(JSON.stringify(si) === '[""]'){
        return <div><label for="sizes">Select size: </label>
        <select name="sizes" id="sizes">
          <option value="n/a">n/a</option>)
        </select></div>
      }
      else{
        console.log(si);
        let sizeelement = <div><label for="sizes">Select size: </label>
          <select name="sizes" id="sizes">
          {si.map((size) => {return <option value={{size}}>{size}</option>})}
          </select>
        </div>
        return sizeelement

      }

    }

    CreatePagePRODUCT(product){
      let productname = product.product_name;
      let image = product.product_picture;
      let productDescription = product.product_description;
      let productPrice = product.product_price;
      let size = product.sizes.split(",");
      console.log(size);
      let subcatagoryname = product.subcategoryname

      let prod = <div className="col">
        <div className="ro">
          <img width = "100%" src={image}/>
        </div>
        <div className="ro">
          <p className="te">
          <p>{productname}</p>
          <p>{productPrice}</p>
          <this.sizerender sizes={size}/>
          <button onClick={() => {this.props.model.addToBasket(product); console.log(product);}}> ADD TO BASKET </button>
          <p>{productDescription}</p>

          </p>
        </div>
      </div>


      return  <div>
        {prod}
        </div>

    }

    CreatePageSHOP(product){
        let productname = product.product_name
        let image = product.product_picture
        let subcatagoryname = product.subcategoryname
        const productcard = <div class="container"><img src={image} style={{"width": "100%"}}  /><div class="overlay">
        <div class="text">{productname}</div></div> </div>;
        return <div className="column">
          <div className="row">
          <Link to={"/"+this.state.sitePosition+"/"+subcatagoryname+"/"+productname}> {productcard} </Link>
          </div>
        </div>
        }

    getPageToCreate(pagedata){
      if(pagedata.fields.sitetype === "html-page"){

        return this.CreatePageHTML(this.state.data.fields.aboutsegment.filter(segment => segment.aboutsegmentname.toLowerCase() === this.state.sitePosition[1].toLowerCase()))
      }
      if(pagedata.fields.sitetype === "shop-page"){
         let shoppage = this.state.data.fields.subcategory.product_in_subcategory.filter(product => product.subcategoryname.toLowerCase() === this.state.sitePosition[1].toLowerCase() && product.product_name.toLowerCase().replace(" ", "%20") === this.state.sitePosition[2].toLowerCase()).map(product => this.CreatePagePRODUCT(product))
            if (shoppage.length === 0){
                shoppage = <p>No product found with this name</p>
            }
        return shoppage
      }

      else{
          return <div dangerouslySetInnerHTML={{ __html:this.state.data.fields.aboutpage}}/>
      }
    }


  render() {
    this.getPageLink();
    return (

      <div className="pageWrapper">        
        <div className="pageTopDivider"></div>
        <div className="center">
        {this.getPageToCreate(this.state.data)} 
        </div>
        <div className="divider"></div>
        <div className="divider"></div>
      </div>
    );
  }


}
