import React from 'react';
import Faq from "./FAQ.js";
import Basket from "./Basket.js";
import Back from "./Back";
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom'
import Butter from "buttercms";

const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77')

export default class Header extends React.Component {
  constructor(props) {
    super();

    this.categorys=[{Atrib:"Men", Categories:["Jeans", "Tshirts","Bags"]}, {Atrib:"Woman", Categories:["Jeans", "Tshirts","Bags"]}, {Atrib:"Info", Categories:["About us", "Retunrs","Shipping"]}]
    this.state = { data: {
                          fields: {navbar: [
                              {
                                type: "category",
                                fields: {
                                  headline: "Men",
                                  categories: [
                                    {
                                      category: "Socks "
                                    },
                                    {
                                      category: "jeans"
                                    },
                                    {
                                      category: "Pens"
                                    }
                                  ]
                                }
                              }
                            ]
                          }

    },
                  showFAQ: false ,
                  showBasket:false
                   };

  }

  async componentDidMount () {
    const resp = await butter.page.retrieve('*', 'navbar');
    console.log(resp);
    this.setState(resp.data);
  }

  RenderBack() {
    let location = useLocation();
    if (location.pathname ==="/"){
        return null;
    }
    else{
        return <Back />;
    }

}

  toggleFAQ() {
    this.setState({
      showFAQ: !this.state.showFAQ,
    });
  }

  toggleBASKET() {
    this.setState({
      showBasket: !this.state.showBasket,
    });
  }

  renderCatagories(category){
    console.log(category);
    let catagoryName = category.fields.headline
    let catagoryCatagory = category.fields.categories
    return <div className="NavUntilHover"><p>{catagoryName}</p>
    <div className="hiddenNavUntilHover">
      <table>
        <tr>{catagoryCatagory.map(cat => {return <tr>{cat.category}</tr>}) }</tr>
      </table>

      </div>
      </div>

    }

  render() {
    console.log("I RENDERED")
    let fields = this.state.data.fields;
    let navbar = this.state.data.fields.navbar
    console.log(this.state);
    console.log("data");
    console.log(this.state.data);
    console.log("fields");
    console.log(fields);
    console.log("navbar");
    console.log(navbar);

    return <div className="header">

        <p className="FAQbutton" onClick={this.toggleFAQ.bind(this)}>CONTACT</p>
  <p className="BASKETbutton" onClick={this.toggleBASKET.bind(this)}>BASKET({()=>{ return this.props.model.getBasketItemNumber().toString()}})</p>
        <Link to="/" style={{color: 'black', "textDecoration":"none" }} >
        <p className = "headerName">Projekt Grön</p>
        </Link>
        <div className="navibar"> 
          {navbar.map(categor => {return this.renderCatagories(categor)})}
        </div>
        <this.RenderBack/>
        <div className="FAQ" >{this.state.showFAQ ? <Faq closeFAQ={this.toggleFAQ.bind(this)} /> : null}</div>
        <div className="FAQ" >{this.state.showBasket ? <Basket closeBASKET={this.toggleBASKET.bind(this)} /> : null}</div>
    </div>


  }





}
