import React from 'react';
import { Link } from "react-router-dom";


export default class Basket extends React.ComponentÂ {

  constructor(props){
    super();
    this.state = {basket:[]}
    this.props = props;

    this.getBasket();
  }

  componentDidMount() {
    this.getBasket();
  }

  componentWillUnmount() {
  }

  getBasket(){
    let bas =  this.props.model.getBasket();
    console.log(bas);
    console.log(this.state.basket)
    if(JSON.stringify(this.state.basket) != JSON.stringify(bas)){
      console.log({bas}.bas);
      this.setState({
        basket:bas
      });

      console.log(this.state);

    }


  }

  basketRender(basket){
    console.log(basket);
    if (basket.length === 0){
      return <table><tr><tr>BASKET I EMPTY</tr></tr></table>
    }
    else{
      return <table>{basket.map(basketItem => <tr><tr>{basketItem.product_name}</tr><tr>{basketItem.product_price}</tr></tr>)}</table>
    }
  }

  clearer(){
    this.props.model.clearBasket(); 



  }

    render() {
      this.getBasket();
      console.log(this.state);

      return (

        <div className='popup' >

          <div className='popup_inner'>
          <div className="divider"></div>
          <div className="divider"></div>
            <h1>BASKET</h1>
            <div className="divider"></div>
            {this.basketRender(this.state.basket)}
            <div className="divider"></div>
            <div className="divider"></div>
            <Link to={"/checkout"}> GO TO CHECKOUT</Link>
            <button onClick={() => this.clearer()}>CLEAR BASKET</button>
          <p className = "closeEx" onClick={this.props.closeBASKET}>x</p>

          </div>
        </div>

      );
    }
  }
