import React from "react";
import { Link } from "react-router-dom";
import Butter from "buttercms";

const butter = Butter('2fa7babaa7064c23b2cb90171957b2add0333e77')

export default class Footer extends React.Component {
  constructor(props) {
    super();
    super(props);
    this.state = { data: {
      fields: {
        footeritem: [
          {
            linktext: "Loading...",
            link: "http://localhost:3000/About/Contact"
          }]
                              }

                          }

    };    

  }

  async componentDidMount () {
    const resp = await butter.page.retrieve('*', 'footer');
    this.setState(resp.data);
    this.setState({itemsInBasket: this.props.model.getBasketItemNumber()})
    this.props.model.addObserver(() => this.forceUpdate());
  }

  renderCatagories(category){
    let catagoryCatagory = category.fields.footeritem;
    let footerlist = catagoryCatagory.map(cat => <li><Link  to={cat.link}>{cat.linktext}</Link></li>)
    return footerlist


    }



  render() {
    let footer = this.state.data;
    const butterimg = "https://cdn.buttercms.com/gZJD5eJjTbCi9MzICoOr"
    return <div className="footer">

        <div className = "divider"></div>

        <ul style={{"border-top": "2px solid #1b1b1b", "padding":"30px" }} className="footerTable">

        {this.renderCatagories(footer)}

        <li className="footerText2">&#xA9; ERIK och SIMON 2020</li>


        </ul>
        <div className = "divider"></div>

        <img style= {{"display": "block;", "margin":"auto;"}} src="https://cdn.buttercms.com/PGJPyIwaQ2KnOA8UyKfH" width="462" height="115"></img>
    </div>
  }




}
